import { useState } from "react";

function BookShow({book, onDelete}) {

  const handleClick = () => {
    onDelete(book.id);
  };

  return (
    <div className="book-show">{book.title}, {book.id}
      <div className="actions">
        <button className="delete" onClick={handleClick}>
          Delete
        </button>
      </div>
    </div>

  );
}

export default BookShow;
